import Layout from '../Components/Layout';
import fetch from 'isomorphic-unfetch';
import React from 'react';
import Forms from '../Components/Forms';

const index = (props)=> (
<Layout>
   <div>

   <Forms all={props.all} />

    <style global jsx>{`
  body{
      background: #333;
  }
  
  
  `}</style>
    </div>
    </Layout>
  
    );

    index.getCountry = async function (e) {
        e.preventDefault();
        const myCountry = e.target.elements.country.value;
        const api = await fetch('https://restcountries.eu/rest/v2/all');
        const data = await api.json();

        console.log(data);




        return{
            all: data.name,
            all: data.capital
        }

    }

   
    export default index;
