class Index extends React.Component{
    state = {
        country: [
            
        ]
    }
componentDidMount() {
    fetch('https://restcountries.eu/rest/v2/all')
    .then(res => res.json())
    .then((res) => {
        this.setState({ country: data.name})
    })
    .catch(console.log)
    console.log(res);
}

        
    

   

    
 
    render(){
        return(

            <Layout> 
            <div>
            <p>{this.props.country}</p>
            <Forms country={this.state.country}
            />
            <style jsx global>{`
            body{
              background: rgb(207, 172, 172);
            }   
            
            `}</style>
            
            </div>
            </Layout>
        );
    }
}

export default Index;
