import Layout from '../Components/Layout';
import fetch from 'isomorphic-unfetch';
import React from 'react';
import Forms from '../Components/Forms';
import Data from '../Components/Data';

class Index extends React.Component{
    state = {
        flag: "",
        country: "",
        population: "",
        borders: "",
        capital: "",
        currency: "",
        region: "",
        error: ""
    }
getCountry = async (e) => {
    e.preventDefault();
    const country = e.target.elements.country.value;

    const api = await fetch(`https://restcountries.eu/rest/v2/name/${country}`);
    const data = await api.json()
    console.log(data);
    if (country){
this.setState({
    flag: data[0].flag,
    country: data[0].name,
    population: data.population,
    capital: data.capital,
    currency: data[0].currencies.name,
    region: data.region,
    error: null
})
}else {
    this.setState({error: "Invalid Input"})
}   
}
 
    render(){
        return(

            <Layout> 
            <div>
            <Forms getCountry={this.getCountry} />
            <Data flag={this.state.flag}
            country={this.state.country}
            population={this.state.country}
            capital={this.state.capital}
            currency={this.state.currency}
            region={this.state.region}
            error={this.state.error}
            />
            <style jsx global>{`
            body{
              background: rgb(207, 172, 172);
            }   
            
            `}</style>
            
            </div>
            </Layout>
        );
    }
}

export default Index;
