class Data extends React.Component{
    
    render(){
        return(
            <div id="container">
            <ul>
            <img className="flag-image"
            src={this.props.flag} alt='Flag'
            />
            {this.props.country && <li>{this.props.country}</li>}
            {this.props.population && <li>{this.props.population}</li>}
            {this.props.capital && <li>{this.props.capital}</li>}
            {this.props.currency && <li>{this.props.currency}</li>}
            {this.props.region && <li>{this.props.region}</li>}
            {this.props.error && <li>{this.props.error}</li>}

            </ul>
            <style jsx>{`
            div{
                text-align: center;
                position: relative;
                justify-content:justify;
            }
            li{
                color: white;
                list-style: none;
            }
            li:nth-child(odd){
                border: grey 1px solid;
            }
            .flag-image{
                width: 400px;
                height: 300px;
                border-radius: 5px;
            }
            `}</style>
            </div>
        )
    }
}

export default Data;
